// App.js (or any higher-level component where you manage state)
import React, { useState } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Product from './Product';
import Cart from './Cart';

function App() {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (item) => {
    // Update the cart state by adding the clicked item
    setCartItems(prevItems => [...prevItems, item]);
  };

  return (
    <>
      <AppRoutes />
    <Router>
      <div>
        <Route exact path="/path" render={() => <Product addToCart={addToCart} />} />
        <Route path="/cart" render={() => <Cart cartItems={cartItems} />} />
      </div>
    </Router>
    </>
  );
}

export default App;
