        // For example, send product data to a backend API
    };
    
    return (
        <div className="container mt-5">
            <h1>Product List</h1>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Delete</th> {/* New column for Delete button */}
                    </tr>
                </thead>
                <tbody>
                    {empList.map(emp => (
                        <tr key={emp.id}>
                            <td>{emp.name}</td>
                            <td>{emp.username}</td>
                            <td>{emp.email}</td>
