import React, { useState } from 'react';

function Payment() {
    const [orderId, setOrderId] = useState('');
    const [selectedProducts, setSelectedProducts] = useState([]); // State variable to store selected products

    const addToCart = (product) => {
        setSelectedProducts([...selectedProducts, product]); // Add product to selectedProducts array
    };

    const fetchOrderId = async () => {
        try {
            const orderData = {
                orderLineItemsListDto: selectedProducts.map(product => ({
                    skuCode: product.skuCode,
                    price: product.price,
                    quantity: product.quantity
                }))
            };

            const response = await fetch('http://localhost:7010/api/order/create', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(orderData)
            });

            if (!response.ok) {
                throw new Error('Failed to create order');
            }

            const data = await response.json();
            setOrderId(data.id);
        } catch (error) {
            console.error('Error creating order:', error);
        }
    };

    const handlePayment = async () => {
        if (!orderId) {
            await fetchOrderId();
        }

        const options = {
            key: 'YOUR_RAZORPAY_KEY_ID',
            amount: 50000,
            currency: 'INR',
            name: 'Acme Corp',
            description: 'Test Transaction',
            image: 'https://example.com/your_logo',
            order_id: orderId,
            handler: function (response) {
                alert(response.razorpay_payment_id);
                alert(response.razorpay_order_id);
                alert(response.razorpay_signature);
            },
            prefill: {
                name: 'John Doe',
                email: 'john.doe@example.com',
                contact: '9999999999'
            },
            notes: {
                address: 'Razorpay Corporate Office'
            },
            theme: {
                color: '#F37254'
            }
        };

        const rzp = new window.Razorpay(options);
        rzp.open();
    
    };

    return (
        <div>
            <h2>Payment Page</h2>
            {/* Example button to add a product to the order */}
            <button onClick={() => addToCart({ skuCode: 'iphone13_mini', price: 4500, quantity: 1 })}>
                Add Product to Order
            </button>
            <button className="btn btn-primary" onClick={handlePayment}>
                Pay Now
            </button>
        </div>
    );
}

export default Payment;
