import React, { useEffect, useState } from 'react';
import { useNavigate} from 'react-router-dom';

function Product() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();


  

  useEffect(() => {
    // Fetch data from the URL
    fetch('http://localhost:7070/api/product/allProducts')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []); // Empty dependency array ensures the effect runs only once

  // Function to handle adding item to cart
  const addToCart = (product) => {
    // Navigate to the Cart component with user data as URL parameter
    navigate(`/cart?name=${product.name}&price=${product.price}&description=${product.description}`);
  };

  return (
    <div className="container">
      <div className="row">
        {/* Mapping over the users array and rendering a card component for each user */}
        {products.map(product => (
          <div key={product.id} className="col-md-4 mb-4">
            <div className="card" style={{ width: '18rem' }}>
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{product.username}</h6>
                <p className="card-text">Price: {product.price}</p>
                <p className="card-text">Description: {product.description}</p>
                &lt;input type="number" value={product.quantity || 1} onChange={(e) => product.quantity = e.target.value} className="form-control mb-2" />;
                {/* Call addToCart function with user data when button is clicked */}
                <button onClick={() => addToCart(product)} className="btn btn-primary">Add to Cart</button>
            
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Product;
