import axios from "axios";
import { useEffect, useState } from "react";

const EmpList = () => {

    const [empList, setEmpList] = useState('');

    useEffect(() => {
        console.log('useEffect');
        axios.get('http://localhost:8000/emp/get-all-emps')
            .then((resp) => {
                console.log(resp.data);
                setEmpList(resp.data);
            })
    }, []);

    return (
        <>

                <div style={{  padding: '2px', border: '1px solid #ccc', borderRadius: '5px', backgroundColor: '#fff' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '10px' }}>Updated data</h2>
                 <div></div>
                    <table className="table table-bordered text-center">
                        <thead className="thead-dark">
                            <tr>
                                <th>FirstName</th>
                                <th>Email</th>
                            </tr>
                        </thead>
                        <tbody>
                            {empList && empList.map(emp =>
                                <tr key={emp.id}>
                                    <td>{emp.firstName}</td>
                                    <td>{emp.email}</td>

                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>


        </>
    );
};

export default EmpList;


