import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Product() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch data from the URL
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []); // Empty dependency array ensures the effect runs only once

  return (
    <div className="container">
      <div className="row">
        {/* Mapping over the users array and rendering a card component for each user */}
        {users.map(user => (
          <div key={user.id} className="col-md-4 mb-4">
            <div className="card" style={{ width: '18rem' }}>
              <div className="card-body">
                <h5 className="card-title">{user.name}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{user.username}</h6>
                <p className="card-text">Email: {user.email}</p>
                <p className="card-text">Phone: {user.phone}</p>
                {/* Use Link to navigate to cart and pass user data as state */}
                <Link to={{ pathname: "/cart", state: { user } }} className="btn btn-primary">Add to Cart</Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Product;
