import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Product() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch data from the URL
    fetch('http://localhost:7070/api/product/allProducts')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []); // Empty dependency array ensures the effect runs only once

  // Function to handle adding item to cart
  const addToCart = (product) => {
    // Here you can modify this function to send the product data to the server and create an order
    fetch('http://localhost:7070/api/order/create', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(product),
    })
      .then(response => response.json())
      .then(data => {
        // Once the order is created, navigate to the payment page with the order ID
        navigate(`/payment?orderId=${data.orderId}`);
      })
      .catch(error => console.error('Error creating order:', error));
  };

  return (
    <div className="container">
      <div className="row">
        {/* Mapping over the products array and rendering a card component for each product */}
        {products.map(product => (
          <div key={product.id} className="col-md-4 mb-4">
            <div className="card" style={{ width: '18rem' }}>
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{product.price}</h6>
                <p className="card-text">{product.description}</p>
                {/* Call addToCart function with product data when button is clicked */}
                <button onClick={() => addToCart(product)} className="btn btn-primary">Add to Cart</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Product;
