import React from 'react';

function Cart({ cartItems }) {
  // Check if cartItems is defined and is an array
  if (!cartItems || !Array.isArray(cartItems)) {
    return <div>No items in the cart</div>;
  }

  const cartItemsList = []; // Initialize an empty array to hold the JSX elements

  // Iterate over the cartItems array using a for loop
  for (let i = 0; i < cartItems.length; i++) {
    const item = cartItems[i];
    cartItemsList.push(
      <li key={i}>
        <div>
          <h4>{item.name}</h4>
          <p>Price: ${item.price}</p>
          <p>Quantity: {item.quantity}</p>
        </div>
      </li>
    );
  }

  return (
    <div>
      <h2>Shopping Cart</h2>
      <ul>
        {cartItems} {/* Render the array of JSX elements */}
      </ul>
    </div>
  );
}

export default Cart;
