import { createSlice } from "@reduxjs/toolkit";
console.log('cartSlice');
const cartSlice = createSlice({
    name: 'cart',
    initialState : { 
        items: [],
        totalQuantity: 0,
      
    },
    reducers : { // more methods 
        setCartObj : (state, action) => {
            console.log(action.payload);
            state.cartObj = action.payload;
        },
        addItem: (state, action) => {
            const existingItem = state.items.find(item => item.id === action.payload.id);
            if (existingItem) {
              existingItem.quantity += 1; // Update quantity for existing item
            } else {
              state.items.push({ ...action.payload, quantity: 1 }); // Add new item with quantity 1
            }
            state.totalQuantity += 1; // Increment total quantity
          },
          updateQuantity: (state, action) => {
            const { id, newQuantity } = action.payload;
            const itemIndex = state.items.findIndex(item => item.id === id);
            if (itemIndex !== -1) { // Check if item exists
              state.items[itemIndex].quantity = newQuantity;
            }
          },
      
    }
});
export default cartSlice.reducer;

export const {cartObj,setAllCart,addItem,updateQuantity} = cartSlice.actions;
