import React from 'react';

function Cart(props) {
  // Check if props.location exists and has a state property
  if (!props.location || !props.location.state) {
    return <p>No data received</p>;
  }

  // Destructure the state object
  const { state } = props.location;

  return (
    <div>
      <h2>Cart</h2>
      {state && state.user ? (
        <div>
          <p>Name: {state.user.name}</p>
          <p>Username: {state.user.username}</p>
          <p>Email: {state.user.email}</p>
          <p>Phone: {state.user.phone}</p>
        </div>
      ) : (
        <p>No user data received</p>
      )}
    </div>
  );
}

export default Cart;
