import axios from "axios";
import { useEffect, useState } from "react";

const EmpList = () => {

    const [empList, setEmpList] = useState('');

    useEffect(() => {
        console.log('useEffect');
        axios.get('http://localhost:8000/emp/get-all-emps')
            .then((resp) => {
                console.log(resp.data);
                setEmpList(resp.data);
            })
    }, []);

    const handleDelete = async (employeeId) => {
        try {
           console.log(employeeId);
            await axios.delete(`http://localhost:8000/emp/delete-emp/${employeeId}`);
            setEmpList(empList.filter(emp => emp.employeeId !== employeeId));
            //setEmpList('');
        } catch (error) {
            console.error('Error deleting employee:', error);
            throw new error(error)
        }


    };

    // const handleUpdate = async (employeeId) => {
    //     try {
    //         const updatedEmployeeData = {
    //             firstName: 'sheela',
    //             email: 'updated@example.com',

    //         };
    //         const response = await axios.put(`http://localhost:8000/emp/update-emp/${employeeId}`, updatedEmployeeData);

    //         // Assuming the backend returns the updated employee data, you can handle it here
    //         console.log('Employee updated:', response.data);

    //         // Refresh employee list or update specific employee in the list if necessary
    //     } catch (error) {
    //         console.error('Error updating employee:', error);
    //         throw new error(error)
    //     }
    // };
    const handleUpdate = async (employeeId,updatedEmployeeData) => {
        try {
            // Send a PUT request to the backend API with the updated employee data
            const response = await axios.put(`http://localhost:8000/emp/update-emp/${employeeId}`, updatedEmployeeData);

            // Log the updated employee data to the console
            console.log('Employee updated:', response.data);

            // You can update the employee list or perform any other necessary actions here
        } catch (error) {
            // Log the error to the console
            console.error('Error updating employee:', error);
            // Rethrow the error to handle it in the calling context if needed
            throw new Error(error);
        }
    };


    return (
        <>
            <div style={{ padding: '2px', border: '1px solid #ccc', borderRadius: '1px', backgroundColor: '#fff' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '5px' }}>Updated data</h2>
                <div>
                    <table className="table table-bordered text-center">
                        <thead className="thead-dark">
                            <tr>
                                <th>FirstName</th>
                                <th>Email</th>
                                <th>Actions</th> 
                            </tr>
                        </thead>
                        <tbody>
                            {empList && empList.map(emp =>
                                <tr key={emp.id}>
                                    <td>{emp.firstName}</td>
                                    <td>{emp.email}</td>
                                    <td>
                                        <button className="btn btn-danger" onClick={() => handleDelete(emp.employeeId)}>Delete</button>
                                        {/* <button className="btn btn-primary" onClick=>Update</button> */}
                                        <button type="submit" className="btn btn-primary" >update</button>

                                    </td> 
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>


        </>
    );
};

export default EmpList;


