import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

function Cart() {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);

  const [carts, setCarts] = useState([]);

  const addCart = () => {
    const newCart = {
      name: searchParams.get('name'),
      username: searchParams.get('username'),
      email: searchParams.get('email'),
      phone: searchParams.get('phone')
    };
    setCarts([...carts, newCart]);
  };

  const removeCart = (index) => {
    const updatedCarts = [...carts];
    updatedCarts.splice(index, 1);
    setCarts(updatedCarts);
  };

  return (
    <div className="container">
      <h2 className="mt-5">Cart</h2>
      {carts.map((cart, index) => (
        <div className="card mt-3" key={index}>
          <div className="card-body">
            {cart.name ? (
              <div>
                <p className="card-text">Name: {cart.name}</p>
                <p className="card-text">Username: {cart.username}</p>
                <p className="card-text">Email: {cart.email}</p>
                <p className="card-text">Phone: {cart.phone}</p>
                <button onClick={() => removeCart(index)} className="btn btn-danger">Remove</button>
              </div>
            ) : (
              <p className="card-text">No user data received</p>
            )}
          </div>
        </div>
      ))}
      {carts.length === 0 && <p className="mt-3">No carts added</p>}
    </div>
  );
}

export default Cart;
