import axios from "axios";
import { useEffect, useState } from "react";

const EmpList = () => {

    const [empList, setEmpList] = useState('');

    useEffect(() => {
        console.log('useEffect');
        axios.get('http://localhost:8000/emp/get-all-emps')
            .then((resp) => {
                console.log(resp.data);
                setEmpList(resp.data);
            })
    }, []);

    const handleDelete = async (emplo) => {
        evt.preventDefault();
        try {

            await axios.delete(`http://localhost:8000/emp/delete-emp/${employeeId}`);


            setEmpList(empList.filter(emp => emp.id !== id));
        } catch (error) {
            console.error('Error deleting employee:', error);
        }
    };







    return (
        <>
            <div style={{ padding: '2px', border: '1px solid #ccc', borderRadius: '1px', backgroundColor: '#fff' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '5px' }}>Updated data</h2>
                <div>
                    <table className="table table-bordered text-center">
                        <thead className="thead-dark">
                            <tr>
                                <th>FirstName</th>
                                <th>Email</th>
                                <th>Actions</th> 
                            </tr>
                        </thead>
                        <tbody>
                            {empList && empList.map(emp =>
                                <tr key={emp.id}>
                                    <td>{emp.firstName}</td>
                                    <td>{emp.email}</td>
                                    <td>
                                        <button className="btn btn-danger" onClick={() => handleDelete(emp.id)}>Delete</button>
                                        {/* <button className="btn btn-primary" onClick={() => handleUpdate(emp.id)}>Update</button> */}
                                    </td> 
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>
            </div>


        </>
    );
};

export default EmpList;


