import { createSlice } from "@reduxjs/toolkit";
console.log('cartSlice');
const cartSlice = createSlice({
    name: 'cart',
    initialState : { 
        items: [],
        totalQuantity: 0,
      
    },
    reducers : { // more methods 
        setCartObj : (state, action) => {
            console.log(action.payload);
            state.cartObj = action.payload;
        },
        addItem: (state, action) => {
            state.items.push(action.payload); // Push new item to the items array
            state.totalQuantity += 1; // Increment total quantity
        },
        updateQuantity: (state, action) => {
            const { id, newQuantity } = action.payload;
            const item = state.items.find(item => item.id === id);
            if (item) {
                item.quantity = newQuantity; // Update quantity of the item
            }
        },
      
    }
});
export default cartSlice.reducer;

export const {cartObj,setAllCart,addItem,updateQuantity} = cartSlice.actions;
